import { CommonModule } from '@angular/common';
import { Component, ElementRef, ViewChild } from '@angular/core';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';

@Component({
  selector: 'app-pvt-ltd',
  imports: [ReactiveFormsModule, CommonModule, FormsModule],
  templateUrl: './pvt-ltd.component.html',
  styleUrl: './pvt-ltd.component.scss'
})
export class PvtLtdComponent {

  companyName: string = '';
  registrationNumber: string = '';
  businessType: string = '';
  lawyerName: string | null = null;
  lawyerFee: number | null = null;
  lawyerEngaged: boolean = false;

  createCompany() {
    // Logic to create company, can be extended to save in a database
    console.log('Company Created:', this.companyName, this.registrationNumber, this.businessType);
  }

  appointLawyer() {
    if (this.lawyerName && this.lawyerFee) {
      this.lawyerEngaged = true;
      console.log(`Lawyer ${this.lawyerName} appointed with a fee of ${this.lawyerFee} per hour.`);
    }
  }

  terminateLawyerAppointment() {
    this.lawyerName = null;
    this.lawyerFee = null;
    this.lawyerEngaged = false;
    console.log('Lawyer appointment terminated.');
  }

  displayCompanyInfo() {
    console.log('Company Info:', {
      companyName: this.companyName,
      registrationNumber: this.registrationNumber,
      businessType: this.businessType,
      lawyerName: this.lawyerName,
      lawyerFee: this.lawyerFee
    });
  }


  images = [
    { src: 'data:image/jpeg;base64,' },
    { src: 'data:image/png;base64,' },
    { src: 'data:image/png;base64,' },
    { src: 'data:image/jpeg;base64,' },
    { src: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTL98sLm0tlLzex50K_2G6zrQ5eUuv2e2u9yw&s' },
    { src: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQuPmh42NMmTkC42xykQfEOdYJOFfK20lFmYA&s' },
     { src: 'data:image/png;base64,' },
    { src: 'data:image/jpeg;base64,' },
    { src: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTL98sLm0tlLzex50K_2G6zrQ5eUuv2e2u9yw&s' },
  ];

  @ViewChild('slider', { static: false }) slider!: ElementRef;
  speed = 1; // Speed of scrolling

  ngAfterViewInit(): void {
    this.startMarquee();
  }

  startMarquee() {
    const sliderElement = this.slider.nativeElement;
    let position = 0;

    const moveSlider = () => {
      position -= this.speed;
      if (Math.abs(position) >= sliderElement.offsetWidth / 2) {
        position = 0; // Reset position to prevent flickering
      }
      sliderElement.style.transform = `translateX(${position}px)`;
      requestAnimationFrame(moveSlider);
    };

    requestAnimationFrame(moveSlider);
  }
}
